import React from 'react'
import Form from 'react-bootstrap/Form'
import { useState, useEffect } from 'react'

const Country = () => {


const [continents, setContinents] = useState([]);
const [continent, setContinent] = useState('worldwide');
const [continentInfo, setContinentInfo] = useState({})

    useEffect (()=>{
        const getData = async () =>{
           await fetch('https://disease.sh/v3/covid-19/continents')
            .then(res=>res.json())
            .then( data => {
                const continents = data.map(item=>(
                    {
                    name : item.continent,
                    value: item.continentInfo
                    }
                ))
                setContinents(continents)
            })           
        }
        getData()
    },[])

    useEffect(() => {

        fetch("https://disease.sh/v3/covid-19/continents")
            .then(res=>res.json())
            .then(data => setContinentInfo(data))
        }, [])
    

    const onContinentChange = async e =>{
        const url = e.target.value ==  'worldwide' ? 'https://disease.sh/v3/covid-19/all' : `https://disease.sh/v3/covid-19/continents/${e.target.value}` 
      
        await fetch(url)
        .then(res=>res.json())
        .then(data=>{
            setContinent(e.target.value);
            setContinentInfo(data)
        })
        
    }

return (
<>
   <div className='container-fluid p-5 mt-5'>
    <h4 className='d-flex justify-content-center '>Select continent</h4> 
    <Form.Select value={continent.name} onChange={onContinentChange}>
            <option value="worldwide">worldwide</option>
            {continents.map(continent => <option value={continent.value}> {continent.name}
            </option>)}
    </Form.Select>
<div className='container-fluid mt-5'>
    
    <table className='table responsive'>
                <thead className='table-active'>
                    <tr>
                        <th>S.N</th>
                        <th>Total</th>
                        <th>Active</th>
                        <th>Recovered</th>
                        <th>Death</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td></td>
                    </tr>
                </tbody>
    </table>
</div>
</div>
        

       
</>
)
}

export default Country
