import React from 'react'
import Form from 'react-bootstrap/Form'
import { useState, useEffect } from 'react'

const Country = () => {
const [data, setData] = useState([]);
const [searchCountries, setSearchCountries] = useState("");
const [result, setResult] = useState([]);
const getdata = async () => {

const res = await fetch('https://disease.sh/v3/covid-19/countries/', {
method: 'GET',
body: JSON.stringify(),
headers: { 'Content-Type': 'application/json' }
})

const actdata = await res.json();
console.log(actdata);
setData(actdata);
}

useEffect (()=>{
getdata();
},[])

const filterCountries = result.filter(item =>{
return searchCountries !=="" ? item.country.includes(searchCountries) : item;
});

const countries = filterCountries.map((actdata, i)=>{
return (
     <>
        <table>
            <tr key={i}>
                    <td>{actdata.country}</td>
                    <td>{data.recovered}</td>
                    <td>{data.active}</td>
                    <td>{data.population}</td>
                    <td>{data.deaths}</td>
                    <td>{data.updated}</td>
                </tr>
        </table>
     </>
);
})


return (
<>
    <div className='container container-fluid mt-5 p-5'>
        <div className='main-heading'>
            <h1 className='mb-2 text-center'> Country wise COVID-19 Dashboard</h1>
        </div>
    </div>

    {/* <Form className='d-flex justify-content-center  p-3'>
        <Form.Group className=" mb-3 row" controlId="formGroupSearch">

            <select className="form-select col-sm-10" aria-label="Default select example">
                <option selected="" onChange={e=>setSearchCountries(e.target.value)} >--Select Country--</option>
                {
                data.map((curEle)=>{
                return(
                <option> {curEle.country} </option>
                )
                })
                }
            </select>
        </Form.Group>
    </Form> */}

    <Form className='d-flex justify-content-center  p-3'>
      <Form.Group className=" mb-3 row" controlId="formGroupSearch">
        <Form.Control className='col-sm-10' type="text" autoComplete='off' placeholder="Search a country" onChange={e=>
          setSearchCountries(e.target.value)}
          />
      </Form.Group>
    </Form>
    <div className=' table-responsive  mx-50px' style={{height:'20em'}}>
        <table id="table" className='table shadow p-3 mb-5 bg-body rounded table-striped '>

            <thead className='bg-dark text-white sticky-top'>
                <tr>
                    <th>S.N</th>
                    <th>Countries</th>
                    <th>Active </th>
                    <th>Recovered</th>
                    <th>Population</th>
                    <th>Death</th>
                    <th>Updated</th>
                </tr>
            </thead>
            <tbody>

                {/* {
                data.map((curEle,index)=>{
                return(
                <tr key={index}>
                    <td>{index +1}</td>
                    <td>{curEle.country}</td>
                    <td>{curEle.active}</td>
                    <td>{curEle.recovered}</td>
                    <td>{curEle.population}</td>
                    <td className=''>{curEle.deaths}</td>
                    <td>{curEle.updated}</td>
                </tr>
                )
                })
                } */}

             

            </tbody>
        </table>
    </div>
</>
)
}

export default Country
