                <tr>
                    <th>S.N</th>
                    <th>Countries</th>
                    <th>Active </th>
                    <th>Recovered</th>
                    <th>Population</th>
                    <th>Death</th>
                    <th>Updated</th>
                </tr>
            </thead>
            <tbody>

                {
                data.map((curEle,index)=>{
                return(
                <tr key={index}>
                    <td>{index +1}</td>
                    <td>{curEle.country}</td>
                    <td>{curEle.active}</td>
                    <td>{curEle.recovered}</td>
                    <td>{curEle.population}</td>
                    <td className=''>{curEle.deaths}</td>
                    <td>{curEle.updated}</td>
                </tr>
                )
                })
                }

            </tbody>
        </table>
    </div> */}
</>
)
}

export default Country
