import React from 'react'
import { useState, useEffect } from 'react'

const Country = () => {
const [data, setData] = useState([]);
const getdata = async () => {

// 1.URL
// 2.Request Method
// 3.Data
// 4.Data Format

const res = await fetch('https://disease.sh/v3/covid-19/countries/', {
method: 'GET',
body: JSON.stringify(),
headers: { 'Content-Type': 'application/json' }
})
const actdata = await res.json();
console.log(actdata);
setData(actdata);
}

useEffect (()=>{
getdata();
},[]) 




return (
<>
    <div className='container container-fluid mt-5 p-5'>
        <div className='main-heading'>
            <h1 className='mb-2 text-center'> Country wise COVID-19 Dashboard</h1>
        </div>
    </div>
    <div>
        <select className="form-select" aria-label="Default select example">
            <option selected="">Open this select menu</option>
           
            
             {
                data.map((curEle,index)=>{
                return(
                <tr key={index}>
                  <option>  {curEle.country}</option>
                   
                </tr>
                )
                })
                } 
        </select>


    </div>
    <div className=' table-responsive  mx-50px' style={{height:'20em'}}>
        <table id="table" className='table shadow p-3 mb-5 bg-body rounded table-striped '>
            
            <thead className='bg-dark text-white sticky-top'>
                <tr>
                    <th>S.N</th>
                    <th>Countries</th>
                    <th>Active </th>
                    <th>Recovered</th>
                    <th>Population</th>
                    <th>Death</th>
                    <th>Updated</th>
                </tr>
            </thead>
            <tbody>

                {
                data.map((curEle,index)=>{
                return(
                <tr key={index}>
                    <td>{index +1}</td>
                    <td>{curEle.country}</td>
                    <td>{curEle.active}</td>
                    <td>{curEle.recovered}</td>
                    <td>{curEle.population}</td>
                    <td className=''>{curEle.deaths}</td>
                    <td>{curEle.updated}</td>
                </tr>
                )
                })
                }

            </tbody>
        </table>
    </div>
</>
)
}

export default Country
