import React,{useEffect} from 'react'

const Covid19 = () => {

    const [data, setData] = useState();

    const getData = async () =>{
         try {
        const res = await fetch('api.covid19india.org/data.json');
        console.log(res);
    } catch (err) {
        console.log(err)
    }
    }
useEffect(() => {
   getData();
}, [])

  return (
    <>
         <div className='container-fluid mt-5'>
            <div className='main-heading'>
                <h1 className='mb-5 text-center'>India's Covid dashboard</h1>
            </div>
        </div>

        <div className='table-responsive'>
           <table className='table table-hover'>
            <thead className='bg-dark text-white'>
                <tr>
                    <th>State</th>
                    <th>Confirmed</th>
                    <th>recovered</th>
                    <th>Active</th>
                    <th>Death</th>
                    <th>Updated</th>
                </tr>
            </thead>
            <tbody>
                {
                    data.map((curEle,index)=>{
                        return(
                            <tr key={index} >
                                <td>{curEle.state}</td>
                                <td>{curEle.confirmed}</td>
                                <td>{curEle.recovered}</td>
                                <td>{curEle.active}</td>
                                <td>{curEle.deaths}</td>
                                <td>{curEle.lastupdatedtime}</td>
                            </tr>
                        )
                    })
                }
                
            </tbody>
           </table>
        </div>
    </>
  )
}

export default Covid19
