import React from 'react'
import { useState, useEffect } from 'react'

const Country = () => {
    const [data, setData] = useState([]);
    const [items, setItems] = useState();
     const getdata = async () => {
         // 1.URL
        // 2.Request Method
        // 3.Data
        // 4.Data Format

        const res = await fetch('https://disease.sh/v3/covid-19/continents/Asia?strict=true', {
            method: 'GET',
            body: JSON.stringify(),
            headers: { 'Content-Type': 'application/json' }
        })
        const actdata = await res.json();
        console.log(res);
        setData(actdata.countries);
        setItems(actdata);
     }

     useEffect (()=>{
        getdata();
     },[])

  return (
   <>
     <div className='container-fluid mt-5'>
        <div className='main-heading'>
            <h1 className='mb-5 text-center'>Cotinent's wise Covid Data</h1>
        </div>
    </div>

    <div className='table-responsive '>
        <table className='table table-hover'>
            <thead className='bg-dark text-white'>
                <tr>
                    <th>Countries</th>
                    <th>Active Cases</th>
                    <th>Recovered</th>
                    <th>Population</th>
                    <th>Death</th>
                    <th>Updated</th>
                </tr>
            </thead>
            <tbody>
                
                {
                data.map((curEle,index)=>{
                return(
                <tr key={index}>
                    <td>{curEle}</td>
                    <td>{curEle.active}</td>
                    <td>{curEle.cases}</td>

                items    

                </tr>
                )
                })
                }

            </tbody>
        </table>
    </div>
   </>
  )
}

export default Country
