import React,{useState, useEffect} from 'react'
import Form from 'react-bootstrap/Form';

const Covid19 = () => {

const [countries, setCountries] = useState([]);
const [country, setCountry] = useState("worldwide");
const [countryInfo, setCountryInfo] = useState({})

    useEffect (()=>{
        const getData = async () =>{
           await fetch('https://disease.sh/v3/covid-19/countries')
            .then(res=>res.json())
            .then( data => {
                const countries = data.map(item=>(
                    {
                    name : item.country,
                    value: item.countryInfo.iso2
                    }
                ))
                setCountries(countries)
            })           
        }
        getData()
    },[])

    useEffect(() => {

        fetch("https://disease.sh/v3/covid-19/all")
            .then(res=>res.json())
            .then(data => setCountryInfo(data))
        }, [])
    

    const onCountryChange =async e =>{
        const url = e.target.value === "worldwide" ? "https://disease.sh/v3/covid-19/all" : "https://disease.sh/v3/covid-19/countries/${e.target.value}" 
       
        await fetch(url)
        .then(res=>res.json())
        .then(data=>{
            setCountry(e.target.value);
            setCountryInfo(data)
        })
        
    }
return (
<>
   <div className='container-fluid p-5 mt-5'>
    <Form>
        <select value={country} onChange={onCountryChange}>
            <option value="worldwide">--worldwide--</option>
            {countries.map(country =>
                <option value={country.value}>{country.name}</option>
            )}
        </select>
    </Form>
</div>
    <div className="row">
          <div className="card col-md text-danger text-center" style={{margin:"10px", backgroundColor:"#fff"}}>
            <div className=" card-body ">
              <div className=" card-title fs-1 fw-bolder ">
               Confirmed
              </div>
              <i className="fa-solid fa-virus-covid fa-6x " />
              <div className='card-text fs-3 lead mt-2 fw-bold'>{countryInfo.todayCases} </div>
            </div>
            <div className='card-footer'>
              <small className=''>Last Updated</small>
            </div>
          </div>
   </div>
   
</>
)
}

export default Covid19
